var mkdirp = require('../').mkdirp,
    mkdirpSync = require('../').mkdirpSync;

mkdirp('/tmp/foo/bar/baz', function (err) {
    if (err) console.error(err)
    else console.log('pow!')
});

try {
    mkdirpSync('/tmp/bar/foo/baz', 0755);
    console.log('double pow!');
}
catch (ex) {
    console.log(ex);
}
